import axios from 'axios'
import React, { useRef } from 'react'

import "./electronics.css"

export default function ElecAdd() {


    const idRef=useRef();
    const imgRef=useRef();
    const brandRef=useRef();
    const priceRef=useRef();
    const ratingRef=useRef();
    const descriptionRef=useRef();



    //    const brand=idRef.current.value
    // const description=idRef.current.value
    // const price=idRef.current.value
    // const rating=idRef.current.value



    // const brand=brandRef.current.value
    // const description=descriptionRef.current.value
    // const price=priceRef.current.value
    // const rating=ratingRef.current.value




    const formHandler=(e)=>{
        e.preventDefault();

         const img=imgRef.current.value

        axios.post("http://localhost:8080/shoes",{
         id:"01",
         img:img,
         brand:"nb",
         description:"new",
         price:200000,
         rating:5
    }
   )
     .then(res=>console.log(res.data))
   }
  return (
    <>



    <div className='add-wrap'>

                <form onSubmit={formHandler}>

                    <input type="text"   ref={idRef} placeholder="id" />
                    <input type="text"    ref={imgRef} placeholder="img" />
                    <input type="text"  placeholder="barnd" />
                    <input type="text"   placeholder="price" />
                    <input type="text"   placeholder="description" />

                    <input type="text"   placeholder="rating" />
                    <input type="submit"></input>

                </form>
    </div>


    </>
  )
}
