import axios from 'axios'
import React, { useRef } from 'react'
import Nav from '../nav/Nav';

import "./electronics.css"

export default function ElecAdd() {


    const idRef=useRef();
    const imgRef=useRef();
    const brandRef=useRef();
    const priceRef=useRef();
    const ratingRef=useRef();
    const descriptionRef=useRef();




    const formHandler=(e)=>{
        e.preventDefault();


        axios.post("http://localhost:8080/electronics",{
         id:"9",
         name:"img",
         rating:"rating",
         img:"brand",

    }
   )
     .then(
        res=>{
        console.log(res.data)
        e.target.reset();
        }


     ).then(err=>console.log(err))
   }
  return (
    <>
        <Nav />


    <div className='add-wrap'>

                <form onSubmit={formHandler}>
                    <input type="text"   ref={idRef} placeholder="id" />
                    <input type="text"    ref={imgRef} placeholder="img" />
                    <input type="text"  ref={brandRef}placeholder="barnd" />
                    <input type="text"  ref={descriptionRef}   placeholder="description" />
                    <input type="text"   ref={priceRef} placeholder="price" />
                    <input type="text"    ref={ratingRef} placeholder="rating" />
                    <input type="submit"></input>
                </form>
    </div>


    </>
  )
}
